    public static IDataTemplate GetToolTemplate(string iconData)
    {
        return new FuncDataTemplate(typeof(object), (o, ns) =>
        {
            var geometry = Geometry.Parse(iconData);

            return new Path()
                .Classes("ToolIcon")
                .Data(geometry)
                .Stretch(Stretch.Fill)
                .Fill(Brushes.White);
        });
    }
